if( msg.payload && msg.payload.page ) {
 msg.page = msg.payload.page * 1;
} else {
    msg.page = 1;
}
if( msg.payload && msg.payload.net ) {
 msg.network = msg.payload.net;
} else {
 msg.network = 1; // 1:keras 2:chainer
}
if( msg.payload && msg.payload.net ) {
 msg.tag = msg.payload.tag;
} else {
 msg.tag = 'zoo';
}

msg.url = "https://api.flickr.com/services/rest/";
msg.url += "?method=flickr.photos.search";
msg.url += "&api_key=976c217175e13ca5570bd700b98d3b74";
msg.url += "&format=json";
msg.url += "&nojsoncallback=1";
//msg.url += "&tags=animal,face,portrait,zoo";
msg.url += "&tags=" + msg.tag;
msg.url += "&tag_mode=all";
msg.url += "&per_page=16";
msg.url += "&page=" + msg.page;
return msg;

if(msg.payload.stat=="fail") {
    msg.payload = msg.payload.message;
    return msg;
}
const photos = msg.payload.photos.photo;
var html = "";

// prev next link to
html += '<div>';
var prev = msg.page-1;
var next = msg.page+1;
if(prev>0) {
    html += '<a href="?page='+prev+'&net='+msg.network+'&tag='+msg.tag+'"> [PREV] </a>';
}
if(next<=msg.payload.photos.pages) {
    html += '<a href="?page='+next+'&net='+msg.network+'&tag='+msg.tag+'"> [NEXT] </a>';
}
html += '</div>';

for(i=0; i<photos.length; i++)
{
    p = photos[i];
    url = 'http://farm'+p.farm+'.static.flickr.com/'+p.server+'/'+p.id+'_'+p.secret;
 html += '<img src="'+url+'_q.jpg"';
    html += ' onclick="doSend(\''+msg.network+' '+url+'.jpg\');"/>';
}

// info
html += '<div class="notranslate">';
html += ' page=' + msg.payload.photos.page;
html += ' pages=' + msg.payload.photos.pages;
html += ' total=' + msg.payload.photos.total;
html += '</div>';

msg.payload = html;
return msg;

const args = msg.payload.split(' ');
const choose = args[0];
const url = args[1];
const file_path = '/tmp/flickr.jpg';

const download = context.global.imagedownloader;
const options = {
    url: url,
    dest: file_path
}
download.image(options)
    .then(( {filename, image }) => {
        msg.payload = file_path;
        msg.choose = choose * 1;
        node.send(msg);
    }).catch((err) => {
        throw err
    })

var sum = 0;
sum += msg.payload[0].per * 1;
sum += msg.payload[1].per * 1;
sum += msg.payload[2].per * 1;

msg.payload[0].pre = msg.payload[0].pre.replace(/_/g," ");
msg.payload[1].pre = msg.payload[1].pre.replace(/_/g," ");
msg.payload[2].pre = msg.payload[2].pre.replace(/_/g," ");

const co = context.global.co;
const chartist = context.global.chartist;
co( function *() {
    const options = {
	width: 300,
	height: 300,
	labelOffset: 20,
	chartPadding: 30,
    };
    const data = {
	labels: [
            msg.payload[0].pre,
            msg.payload[1].pre,
            msg.payload[2].pre,
            "unknown"
	],
	series: [
            msg.payload[0].per * 1,
            msg.payload[1].per * 1,
            msg.payload[2].per * 1,
            100 - sum
	]
    };


    var table = '<table>';
    table += '<tr>';
    table += '<th class="notranslate">' + ['','Keras','Chainer'][msg.choose];
    table += '<th>' + msg.elapsed + " sec";
    table += '<tr>';
    table += '<td>' + msg.payload[0].pre;
    table += '<td>' + msg.payload[0].per + '%';
    table += '<tr>';
    table += '<td>' + msg.payload[1].pre;
    table += '<td>' + msg.payload[1].per + '%';
    table += '<tr>';
    table += '<td>' + msg.payload[2].pre;
    table += '<td>' + msg.payload[2].per + '%';
    table += '</table>';

    const chart = yield chartist('pie', options, data);

    msg.payload = '<table><tr><td>'+chart+'<td>'+table+'</table>';
    node.send(msg);
});
